
const throttleFunction=(func, delay)=>{
	let prev = 0;
	return (...args) => {
		let now = new Date().getTime();
	/*	console.log(now-prev, delay);*/
	
		if(now - prev> delay){
		prev = now;
		return func(...args);
		}
	}
	}
var flag = 0;
var box = document.querySelector("#rec");
box.addEventListener("mousemove",throttleFunction(function(dets){
     var div = document.createElement("div");
     var img = document.createElement("img");
       div.classList.add("newdivg");
       document.body.appendChild(div);
       div.style.top = dets.clientY+'px';
       div.style.left = dets.clientX+'px';
       if(flag==0){
       img.setAttribute("src","data:image/jpeg;base64,");
       flag = 1;
       }
       else if(flag==1){
          img.setAttribute("src","data:image/jpeg;base64,");
           flag = 2;
       }
       else{
          img.setAttribute("src","data:image/jpeg;base64,");
          flag = 0;
       }

       div.appendChild(img);
        gsap.to(img,{
            yPercent:-100,
            ease:Power3,
        })
        setTimeout(function(){
            div.remove();
        },2000)
     console.log(div+dets.clientX);
},400));
